import React, { useState, useRef, useEffect } from 'react';

const Chat = () => {
  // State for managing messages and input
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Auto-scroll to bottom when new messages arrive
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Handle sending messages to backend
  const sendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage = inputValue.trim();
    setInputValue('');
    setIsLoading(true);

    // Add user message to chat immediately
    setMessages(prev => [...prev, { type: 'user', content: userMessage }]);

    try {
      // Send POST request to backend
      const response = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ prompt: userMessage }),
      });

      if (!response.ok) {
        throw new Error('Failed to get response from server');
      }

      // Handle streaming response
      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let botMessage = '';

      // Add initial bot message placeholder
      setMessages(prev => [...prev, { type: 'bot', content: '' }]);

      // Read stream chunks
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value);
        const lines = chunk.split('\n');

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            try {
              const data = JSON.parse(line.slice(6));
              if (data.response) {
                botMessage += data.response;
                // Update the last message (bot message) with accumulated content
                setMessages(prev => {
                  const newMessages = [...prev];
                  newMessages[newMessages.length - 1] = { 
                    type: 'bot', 
                    content: botMessage 
                  };
                  return newMessages;
                });
              }
            } catch (e) {
              // Skip malformed JSON chunks
              continue;
            }
          }
        }
      }
    } catch (error) {
      console.error('Error sending message:', error);
      // Add error message to chat
      setMessages(prev => [...prev, { 
        type: 'bot', 
        content: 'Sorry, I encountered an error. Please try again.' 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle Enter key press
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div style={styles.chatContainer}>
      {/* Chat Messages Area */}
      <div style={styles.messagesContainer}>
        {messages.length === 0 && (
          <div style={styles.welcomeMessage}>
            Welcome! Start a conversation with the chatbot.
          </div>
        )}
        
        {messages.map((message, index) => (
          <div
            key={index}
            style={{
              ...styles.message,
              ...(message.type === 'user' ? styles.userMessage : styles.botMessage)
            }}
          >
            <div style={styles.messageContent}>
              {message.content}
            </div>
          </div>
        ))}
        
        {isLoading && (
          <div style={{...styles.message, ...styles.botMessage}}>
            <div style={styles.messageContent}>
              <div style={styles.typingIndicator}>
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <div style={styles.inputContainer}>
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
          style={styles.input}
          disabled={isLoading}
        />
        <button
          onClick={sendMessage}
          disabled={!inputValue.trim() || isLoading}
          style={{
            ...styles.sendButton,
            ...(isLoading || !inputValue.trim() ? styles.sendButtonDisabled : {})
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

// Inline styles for minimal but neat appearance
const styles = {
  chatContainer: {
    display: 'flex',
    flexDirection: 'column',
    height: '500px',
    maxWidth: '600px',
    margin: '0 auto',
    border: '1px solid #ddd',
    borderRadius: '8px',
    backgroundColor: '#f9f9f9',
    fontFamily: 'Arial, sans-serif',
  },
  
  messagesContainer: {
    flex: 1,
    overflowY: 'auto',
    padding: '16px',
    display: 'flex',
    flexDirection: 'column',
    gap: '12px',
  },
  
  welcomeMessage: {
    textAlign: 'center',
    color: '#666',
    fontStyle: 'italic',
    margin: '20px 0',
  },
  
  message: {
    maxWidth: '80%',
    padding: '12px 16px',
    borderRadius: '18px',
    wordWrap: 'break-word',
  },
  
  userMessage: {
    alignSelf: 'flex-end',
    backgroundColor: '#007bff',
    color: 'white',
    marginLeft: 'auto',
  },
  
  botMessage: {
    alignSelf: 'flex-start',
    backgroundColor: 'white',
    color: '#333',
    border: '1px solid #e0e0e0',
    marginRight: 'auto',
  },
  
  messageContent: {
    lineHeight: '1.4',
  },
  
  typingIndicator: {
    display: 'flex',
    gap: '4px',
    alignItems: 'center',
  },
  
  typingIndicator: {
    display: 'flex',
    gap: '4px',
    alignItems: 'center',
  },
  
  'typingIndicator span': {
    width: '8px',
    height: '8px',
    borderRadius: '50%',
    backgroundColor: '#999',
    animation: 'typing 1.4s infinite ease-in-out',
  },
  
  inputContainer: {
    display: 'flex',
    padding: '16px',
    borderTop: '1px solid #ddd',
    backgroundColor: 'white',
    gap: '8px',
  },
  
  input: {
    flex: 1,
    padding: '12px 16px',
    border: '1px solid #ddd',
    borderRadius: '24px',
    fontSize: '14px',
    outline: 'none',
  },
  
  sendButton: {
    padding: '12px 24px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '24px',
    cursor: 'pointer',
    fontSize: '14px',
    fontWeight: 'bold',
    transition: 'background-color 0.2s',
  },
  
  sendButtonDisabled: {
    backgroundColor: '#ccc',
    cursor: 'not-allowed',
  },
};

export default Chat;
